package basicprograms;

public class Practice1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//sum,subtraction,MultiFication,Division of Two Numbers
//Sum
		double value1=123456.1258749;
		float value2=145268.12f;
		double answer=(value1+value2);
		
		System.out.println("Value1 = "+value1);
		System.out.println("Value2 = "+value2);
		System.out.println("Answer after Addition of Value1&Value2  = "+ answer);
		System.out.println("--*-----------*-------------*--------------*---------------");
		
		value1=123456.1258749;
		value2=145268.12f;
		answer=(value1-value2);
		
		System.out.println("Value1 = "+value1);
		System.out.println("Value2 = "+value2);
		System.out.println("Answer after Subtraction of Value1&Value2  = "+ answer);
		System.out.println("--*-----------*-------------*--------------*---------------");
		
		value1=394.12;
		value2=4.12f;
		answer=(value1*value2);
		System.out.println("Value1 = "+value1);
		System.out.println("Value2 = "+value2);
		System.out.println("Answer after Multification of Value1&Value2  = "+answer);
		System.out.println("--*---------- -*-------------*--------------*---------------");
		
		
     
		
	}

}
